import { useState } from 'react';

export default function Sidebar({
  isOpen,
  isDarkMode,
  sessions,
  currentSessionId,
  onSelectSession,
  onNewChat,
  onDeleteSession,
  isLoading
}) {
  const [deleteConfirm, setDeleteConfirm] = useState(null);

  const handleDelete = (e, sessionId) => {
    e.stopPropagation();
    if (deleteConfirm === sessionId) {
      onDeleteSession(sessionId);
      setDeleteConfirm(null);
    } else {
      setDeleteConfirm(sessionId);
      setTimeout(() => setDeleteConfirm(null), 3000);
    }
  };

  const formatDate = (isoDate) => {
    const date = new Date(isoDate);
    const now = new Date();
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);

    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    if (diffDays < 7) return `${diffDays}d ago`;
    return date.toLocaleDateString();
  };

  if (!isOpen) return null;

  return (
    <div className={`w-80 border-r flex flex-col transition-colors duration-300 ${
      isDarkMode ? 'bg-gray-900 border-gray-700' : 'bg-white border-gray-200'
    }`}>
      {/* Sidebar Header */}
      <div className="p-4 border-b ${isDarkMode ? 'border-gray-700' : 'border-gray-200'}">
        <button
          onClick={onNewChat}
          className={`w-full py-3 px-4 rounded-lg font-medium transition-all duration-200 flex items-center justify-center space-x-2 ${
            isDarkMode
              ? 'bg-gradient-to-r from-green-600 to-emerald-700 text-white hover:from-green-700 hover:to-emerald-800'
              : 'bg-gradient-to-r from-green-500 to-emerald-600 text-white hover:from-green-600 hover:to-emerald-700'
          } shadow-lg hover:shadow-xl transform hover:-translate-y-0.5`}
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
          </svg>
          <span>New Chat</span>
        </button>
      </div>

      {/* Sessions List */}
      <div className="flex-1 overflow-y-auto custom-scrollbar">
        {isLoading ? (
          <div className="p-4 text-center">
            <div className={`inline-block animate-spin rounded-full h-8 w-8 border-b-2 ${
              isDarkMode ? 'border-green-400' : 'border-green-600'
            }`}></div>
            <p className={`mt-2 text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
              Loading sessions...
            </p>
          </div>
        ) : sessions.length === 0 ? (
          <div className="p-4 text-center">
            <div className={`text-6xl mb-4`}>ðŸ’¬</div>
            <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
              No conversations yet
            </p>
            <p className={`text-xs mt-2 ${isDarkMode ? 'text-gray-500' : 'text-gray-500'}`}>
              Start a new chat to begin
            </p>
          </div>
        ) : (
          <div className="p-2 space-y-2">
            {sessions.map((session) => (
              <div
                key={session.id}
                onClick={() => onSelectSession(session.id)}
                className={`group relative p-4 rounded-lg cursor-pointer transition-all duration-200 ${
                  currentSessionId === session.id
                    ? isDarkMode
                      ? 'bg-gray-800 border-2 border-green-600'
                      : 'bg-gradient-to-r from-green-50 to-blue-50 border-2 border-green-500'
                    : isDarkMode
                      ? 'bg-gray-800 border border-gray-700 hover:bg-gray-750 hover:border-gray-600'
                      : 'bg-gray-50 border border-gray-200 hover:bg-gray-100 hover:border-gray-300'
                }`}
              >
                {/* Session Name */}
                <div className={`font-medium mb-1 pr-8 truncate ${
                  isDarkMode ? 'text-gray-200' : 'text-gray-800'
                }`}>
                  {session.name}
                </div>

                {/* Session Preview */}
                <div className={`text-xs mb-2 truncate ${
                  isDarkMode ? 'text-gray-400' : 'text-gray-600'
                }`}>
                  {session.preview || 'No messages yet'}
                </div>

                {/* Session Meta */}
                <div className="flex items-center justify-between text-xs">
                  <span className={isDarkMode ? 'text-gray-500' : 'text-gray-500'}>
                    {formatDate(session.updated_at)}
                  </span>
                  <span className={`px-2 py-1 rounded ${
                    isDarkMode ? 'bg-gray-700 text-gray-400' : 'bg-gray-200 text-gray-600'
                  }`}>
                    {session.message_count} msgs
                  </span>
                </div>

                {/* Delete Button */}
                <button
                  onClick={(e) => handleDelete(e, session.id)}
                  className={`absolute top-2 right-2 p-2 rounded-lg opacity-0 group-hover:opacity-100 transition-all duration-200 ${
                    deleteConfirm === session.id
                      ? 'bg-red-500 text-white'
                      : isDarkMode
                        ? 'bg-gray-700 text-gray-300 hover:bg-red-600 hover:text-white'
                        : 'bg-white text-gray-600 hover:bg-red-500 hover:text-white'
                  }`}
                  title={deleteConfirm === session.id ? 'Click again to confirm' : 'Delete session'}
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                  </svg>
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Sidebar Footer */}
      <div className={`p-4 border-t text-xs text-center ${
        isDarkMode ? 'border-gray-700 text-gray-500' : 'border-gray-200 text-gray-500'
      }`}>
        <p>Islamic AI Assistant</p>
        <p className="mt-1">Based on authentic sources</p>
      </div>
    </div>
  );
}
