import { useState, useRef, useEffect } from "react";
import { askQuestion, getSessionMessages } from "./api";

export default function Chat({ isDarkMode, sessionId, onSessionUpdate }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [loadingSession, setLoadingSession] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Load session messages when sessionId changes
  useEffect(() => {
    if (sessionId) {
      loadSessionMessages(sessionId);
    } else {
      setMessages([]);
    }
  }, [sessionId]);

  const loadSessionMessages = async (sid) => {
    setLoadingSession(true);
    try {
      const sessionMessages = await getSessionMessages(sid);
      if (sessionMessages && sessionMessages.length > 0) {
        setMessages(sessionMessages);
      } else {
        setMessages([]);
      }
    } catch (error) {
      console.error("Error loading session:", error);
      setMessages([]);
    } finally {
      setLoadingSession(false);
    }
  };

  const sendMessage = async () => {
    if (!input.trim() || loading) return;
    
    const userMessage = input.trim();
    setInput("");
    
    // Optimistically add user message
    setMessages(prev => [...prev, { 
      role: "user", 
      content: userMessage, 
      timestamp: new Date().toISOString() 
    }]);
    setLoading(true);

    try {
      const response = await askQuestion(userMessage, sessionId);
      
      // Add bot response
      setMessages(prev => [...prev, { 
        role: "bot", 
        content: response.answer,
        timestamp: new Date().toISOString()
      }]);
      
      // Update session list
      if (onSessionUpdate) {
        onSessionUpdate();
      }
    } catch (error) {
      setMessages(prev => [...prev, { 
        role: "bot", 
        content: "As-salamu alaykum. I apologize, but I'm having trouble connecting to the knowledge base. Please try again later.",
        timestamp: new Date().toISOString()
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const welcomeMessage = {
    role: "bot",
    content: "As-salamu alaykum! I'm your Islamic AI assistant. I can answer questions based on Quran, Hadith, and authentic Islamic sources. How can I help you today?",
    timestamp: new Date().toISOString()
  };

  const displayMessages = messages.length > 0 ? messages : [welcomeMessage];

  if (loadingSession) {
    return (
      <div className={`flex-1 flex items-center justify-center ${
        isDarkMode ? 'bg-gray-900' : 'bg-white'
      }`}>
        <div className="text-center">
          <div className={`inline-block animate-spin rounded-full h-12 w-12 border-b-2 ${
            isDarkMode ? 'border-green-400' : 'border-green-600'
          }`}></div>
          <p className={`mt-4 ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            Loading conversation...
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Messages Area */}
      <div className={`flex-1 overflow-y-auto p-4 md:p-6 space-y-4 md:space-y-6 custom-scrollbar ${
        isDarkMode ? 'bg-gray-900' : 'bg-white'
      }`}>
        {displayMessages.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.role === "user" ? "justify-end" : "justify-start"} animate-fadeIn`}
          >
            <div className={`flex max-w-[90%] md:max-w-[80%] lg:max-w-[70%] ${
              message.role === "user" ? "flex-row-reverse" : "flex-row"
            } items-start space-x-2 md:space-x-3`}>
              
              {/* Avatar */}
              <div className={`flex-shrink-0 w-6 h-6 md:w-8 md:h-8 rounded-full flex items-center justify-center text-white text-xs md:text-sm font-bold ${
                message.role === "user" 
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600' 
                  : 'bg-gradient-to-r from-green-500 to-emerald-600'
              }`}>
                {message.role === "user" ? "You" : "AI"}
              </div>
              
              {/* Message Bubble */}
              <div className={`rounded-2xl p-3 md:p-4 shadow-sm ${
                message.role === "user"
                  ? isDarkMode
                    ? 'bg-blue-600 text-white rounded-br-none'
                    : 'bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-br-none'
                  : isDarkMode
                    ? 'bg-gray-800 text-gray-100 border border-gray-700 rounded-bl-none'
                    : 'bg-gradient-to-r from-green-50 to-blue-50 text-gray-800 border border-green-100 rounded-bl-none'
              }`}>
                <div className="whitespace-pre-wrap leading-relaxed text-sm md:text-base">
                  {message.content}
                </div>
                <div className={`text-[10px] md:text-xs mt-2 ${
                  message.role === "user" 
                    ? 'text-blue-100' 
                    : isDarkMode ? 'text-gray-400' : 'text-gray-500'
                }`}>
                  {new Date(message.timestamp || Date.now()).toLocaleTimeString([], { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </div>
              </div>
            </div>
          </div>
        ))}
        
        {/* Loading Indicator */}
        {loading && (
          <div className="flex justify-start animate-pulse">
            <div className="flex items-start space-x-2 md:space-x-3 max-w-[90%] md:max-w-[80%] lg:max-w-[70%]">
              <div className="flex-shrink-0 w-6 h-6 md:w-8 md:h-8 rounded-full bg-gradient-to-r from-green-500 to-emerald-600 flex items-center justify-center text-white text-xs md:text-sm font-bold">
                AI
              </div>
              <div className={`rounded-2xl p-3 md:p-4 ${
                isDarkMode
                  ? 'bg-gray-800 border border-gray-700 rounded-bl-none'
                  : 'bg-gradient-to-r from-green-50 to-blue-50 border border-green-100 rounded-bl-none'
              }`}>
                <div className="flex items-center space-x-2">
                  <div className="flex space-x-1">
                    <div className="w-1.5 h-1.5 md:w-2 md:h-2 bg-green-500 rounded-full animate-bounce"></div>
                    <div className="w-1.5 h-1.5 md:w-2 md:h-2 bg-green-500 rounded-full animate-bounce" style={{ animationDelay: "0.1s" }}></div>
                    <div className="w-1.5 h-1.5 md:w-2 md:h-2 bg-green-500 rounded-full animate-bounce" style={{ animationDelay: "0.2s" }}></div>
                  </div>
                  <span className={`text-xs md:text-sm ${isDarkMode ? 'text-gray-400' : 'text-green-600'}`}>
                    Thinking...
                  </span>
                </div>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      
      {/* Input Area */}
      <div className={`border-t p-3 md:p-4 ${
        isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-gray-50 border-gray-200'
      }`}>
        <div className="flex space-x-2 md:space-x-4">
          <div className="flex-1 relative">
            <textarea
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask a question..."
              className={`w-full border rounded-xl md:rounded-2xl p-3 md:p-4 pr-12 resize-none focus:outline-none focus:ring-2 transition-all text-sm md:text-base ${
                isDarkMode
                  ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400 focus:ring-green-500'
                  : 'bg-white border-gray-300 text-gray-800 placeholder-gray-500 focus:ring-green-500'
              }`}
              rows={2}
              disabled={loading}
            />
            <div className="absolute right-3 bottom-3 text-gray-400 text-xs hidden md:block">
              â†µ Enter
            </div>
          </div>
          <button
            onClick={sendMessage}
            disabled={loading || !input.trim()}
            className={`px-4 md:px-6 py-2 md:py-3 rounded-xl md:rounded-2xl font-medium transition-all flex items-center justify-center ${
              loading || !input.trim()
                ? isDarkMode
                  ? 'bg-gray-700 text-gray-500'
                  : 'bg-gray-200 text-gray-400'
                : 'bg-gradient-to-r from-green-500 to-emerald-600 text-white hover:from-green-600 hover:to-emerald-700 shadow-lg'
            }`}
          >
            <svg className="w-4 h-4 md:w-5 md:h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
            </svg>
          </button>
        </div>

        {/* Quick Suggestions (only show on new chat) */}
        {displayMessages.length === 1 && displayMessages[0].role === "bot" && (
          <div className="mt-3 flex flex-wrap gap-2">
            {[
              "What are the five pillars of Islam?",
              "How to perform wudu?",
              "Zakat calculation rules",
              "Hanafi ruling on music"
            ].map((suggestion, index) => (
              <button
                key={index}
                onClick={() => setInput(suggestion)}
                className={`px-2 py-1 md:px-3 md:py-2 rounded-full text-xs md:text-sm transition-all ${
                  isDarkMode
                    ? 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                    : 'bg-white text-gray-600 hover:bg-gray-100 border border-gray-200'
                }`}
              >
                {suggestion}
              </button>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
