import { useState } from "react";
import { askQuestion } from "./api";

export default function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input) return;
    setMessages([...messages, { role: "user", content: input }]);

    // Use HTTP POST via axios
    const answer = await askQuestion(input);

    setMessages([...messages, 
      { role: "user", content: input }, 
      { role: "bot", content: answer }
    ]);
    setInput("");
  };

  return (
    <div className="p-4">
      <div className="h-[400px] overflow-y-auto border p-2 mb-2">
        {messages.map((m, i) => (
          <div key={i} className={m.role === "user" ? "text-right" : "text-left"}>
            <span className={m.role === "user" ? "bg-blue-200 p-2 rounded" : "bg-gray-200 p-2 rounded"}>
              {m.content}
            </span>
          </div>
        ))}
      </div>
      <input
        type="text"
        className="border p-2 w-full"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && sendMessage()}
      />
      <button onClick={sendMessage} className="bg-blue-500 text-white px-4 py-2 mt-2 rounded">
        Send
      </button>
    </div>
  );
}
