import { useState, useRef, useEffect } from "react";
import { askQuestion } from "./api";

export default function Chat({ isDarkMode, sessionId }) {
  const [messages, setMessages] = useState([
    { 
      role: "bot", 
      content: "As-salamu alaykum! I'm your Islamic AI assistant. I can answer questions based on Quran, Hadith, and authentic Islamic sources. How can I help you today?" 
    }
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Focus input on load
  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  // When session changes
  useEffect(() => {
    setMessages([
      { 
        role: "bot", 
        content: sessionId === "default" 
          ? "As-salamu alaykum! I'm your Islamic AI assistant. I can answer questions based on Quran, Hadith, and authentic Islamic sources. How can I help you today?" 
          : "As-salamu alaykum! Welcome to this chat. I'm your Islamic AI assistant. How can I help you today?" 
      }
    ]);
  }, [sessionId]);

  const sendMessage = async () => {
    if (!input.trim() || loading) return;
    
    const userMessage = input.trim();
    setInput("");
    setMessages(prev => [...prev, { 
      role: "user", 
      content: userMessage,
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    }]);
    setLoading(true);
    setIsTyping(true);

    try {
      const answer = await askQuestion(userMessage, sessionId);
      setIsTyping(false);
      setMessages(prev => [...prev, { 
        role: "bot", 
        content: answer,
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
    } catch (error) {
      console.error("Error:", error);
      setIsTyping(false);
      setMessages(prev => [...prev, { 
        role: "bot", 
        content: "As-salamu alaykum. I apologize, but I'm having trouble connecting to the knowledge base. Please try again later or consult with a local scholar.",
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const clearChat = () => {
    setMessages([
      { 
        role: "bot", 
        content: sessionId === "default"
          ? "As-salamu alaykum! The conversation has been cleared. How can I assist you with Islamic knowledge today?"
          : "As-salamu alaykum! The conversation has been cleared. How can I assist you with Islamic knowledge today?" 
      }
    ]);
  };

  const formatMessageTime = (timestamp) => {
    if (!timestamp) return '';
    return timestamp;
  };

  // Quick suggestions
  const suggestions = [
    "What are the five pillars of Islam?",
    "How to perform wudu?",
    "Zakat calculation rules",
    "Hanafi ruling on music",
    "Importance of Friday prayer",
    "Rules of fasting in Ramadan"
  ];

  return (
    <div className="flex flex-col h-full">
      {/* Chat Header */}
      <div className={`border-b ${
        isDarkMode ? 'border-gray-700 bg-gradient-to-r from-gray-900 to-gray-800' : 'border-gray-200 bg-gradient-to-r from-green-50 to-blue-50'
      } p-3 sm:p-4 flex justify-between items-center chat-header`}>
        <div className="flex items-center space-x-2 sm:space-x-3">
          <div className={`w-2 h-2 sm:w-3 sm:h-3 rounded-full ${
            loading ? 'bg-yellow-400 animate-pulse' : 'bg-green-500'
          }`}></div>
          <div>
            <span className={`font-medium text-sm sm:text-base ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
              {loading ? 'Searching sources...' : 'Online'}
            </span>
            <div className="flex items-center space-x-2">
              {sessionId !== "default" && (
                <span className={`text-xs px-2 py-0.5 rounded-full ${
                  isDarkMode 
                    ? 'bg-gray-700 text-gray-300' 
                    : 'bg-gray-100 text-gray-600'
                }`}>
                  Chat ID: {sessionId.slice(0, 6)}...
                </span>
              )}
              <span className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                {messages.length} message{messages.length !== 1 ? 's' : ''}
              </span>
            </div>
          </div>
        </div>
        <div className="flex items-center space-x-2">
          <button
            onClick={clearChat}
            className={`px-3 py-1.5 sm:px-4 sm:py-2 rounded-lg text-xs sm:text-sm font-medium transition-all duration-200 hover:scale-105 ${
              isDarkMode
                ? 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            Clear
          </button>
        </div>
      </div>

      {/* Messages Area */}
      <div className={`flex-1 overflow-y-auto p-3 sm:p-4 md:p-6 space-y-4 sm:space-y-6 custom-scrollbar chat-grid ${
        isDarkMode ? 'bg-gray-900/50' : 'bg-white/50'
      }`}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.role === "user" ? "justify-end" : "justify-start"} message-bubble`}
          >
            <div className={`flex max-w-[90%] sm:max-w-[85%] ${message.role === "user" ? "flex-row-reverse" : "flex-row"} items-start space-x-2 sm:space-x-3`}>
              {/* Avatar */}
              <div className={`flex-shrink-0 w-7 h-7 sm:w-8 sm:h-8 rounded-full flex items-center justify-center text-white text-xs sm:text-sm font-bold shadow-lg ${
                message.role === "user" 
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600' 
                  : 'bg-gradient-to-r from-green-500 to-emerald-600'
              }`}>
                {message.role === "user" ? "You" : "AI"}
              </div>
              
              {/* Message Bubble */}
              <div className={`rounded-2xl p-3 sm:p-4 shadow-sm relative max-w-full ${
                message.role === "user"
                  ? isDarkMode
                    ? 'bg-gradient-to-r from-blue-600 to-purple-700 text-white rounded-br-none'
                    : 'bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-br-none'
                  : isDarkMode
                    ? 'bg-gradient-to-r from-gray-800 to-gray-900 text-gray-100 border border-gray-700 rounded-bl-none'
                    : 'bg-gradient-to-r from-green-50 to-blue-50 text-gray-800 border border-green-100 rounded-bl-none'
              }`}>
                <div className="whitespace-pre-wrap leading-relaxed text-sm sm:text-[15px] break-words">
                  {message.content}
                </div>
                <div className="flex justify-between items-center mt-2">
                  <div className={`text-xs ${
                    message.role === "user" 
                      ? 'text-blue-100/70' 
                      : isDarkMode ? 'text-gray-400' : 'text-gray-500'
                  }`}>
                    {message.role === "user" ? 'You' : 'Islamic Assistant'}
                  </div>
                  {message.timestamp && (
                    <div className={`text-xs ${
                      message.role === "user" 
                        ? 'text-blue-100/50' 
                        : isDarkMode ? 'text-gray-500' : 'text-gray-400'
                    }`}>
                      {formatMessageTime(message.timestamp)}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        ))}
        
        {/* Typing Indicator */}
        {isTyping && (
          <div className="flex justify-start">
            <div className="flex items-start space-x-3 max-w-[85%]">
              <div className="flex-shrink-0 w-8 h-8 rounded-full bg-gradient-to-r from-green-500 to-emerald-600 flex items-center justify-center text-white text-sm font-bold shadow-lg">
                AI
              </div>
              <div className={`rounded-2xl p-4 shadow-sm ${
                isDarkMode
                  ? 'bg-gradient-to-r from-gray-800 to-gray-900 text-gray-100 border border-gray-700 rounded-bl-none'
                  : 'bg-gradient-to-r from-green-50 to-blue-50 text-gray-800 border border-green-100 rounded-bl-none'
              }`}>
                <div className="flex items-center space-x-2">
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 bg-green-500 rounded-full typing-dot"></div>
                    <div className="w-2 h-2 bg-green-500 rounded-full typing-dot"></div>
                    <div className="w-2 h-2 bg-green-500 rounded-full typing-dot"></div>
                  </div>
                  <span className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-green-600'} font-medium`}>
                    Searching Islamic sources...
                  </span>
                </div>
                <p className={`text-xs mt-2 ${isDarkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                  Using chat memory from previous messages
                </p>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} className="h-4" />
      </div>
      
      {/* Input Area */}
      <div className={`border-t p-3 sm:p-4 md:p-6 input-container ${
        isDarkMode ? 'bg-gray-800/80 border-gray-700' : 'bg-gray-50/80 border-gray-200'
      }`}>
        <div className="flex flex-col sm:flex-row sm:space-x-4 space-y-4 sm:space-y-0">
          <div className="flex-1 relative">
            <textarea
              ref={inputRef}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask your question about Islam (prayer, fasting, zakat, fiqh rulings, etc.)..."
              className={`w-full border rounded-2xl p-3 sm:p-4 pr-10 sm:pr-12 resize-none focus:outline-none focus:ring-2 transition-all duration-200 custom-scrollbar text-sm sm:text-base ${
                isDarkMode
                  ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400 focus:ring-green-500 focus:border-green-500'
                  : 'bg-white border-gray-300 text-gray-800 placeholder-gray-500 focus:ring-green-500 focus:border-green-500'
              }`}
              rows="2"
              disabled={loading}
            />
            <div className="absolute right-3 bottom-3 text-gray-400 text-xs sm:text-sm hidden sm:block">
              ↵ Enter to send
            </div>
            <div className="absolute right-3 bottom-3 text-gray-400 text-xs sm:hidden">
              ↵ Send
            </div>
          </div>
          <button
            onClick={sendMessage}
            disabled={loading || !input.trim()}
            className={`self-end px-4 sm:px-6 py-2.5 sm:py-3 rounded-2xl font-medium transition-all duration-200 flex items-center justify-center space-x-2 ${
              loading || !input.trim()
                ? isDarkMode
                  ? 'bg-gray-700 text-gray-500 cursor-not-allowed'
                  : 'bg-gray-200 text-gray-400 cursor-not-allowed'
                : 'bg-gradient-to-r from-green-500 to-emerald-600 text-white hover:from-green-600 hover:to-emerald-700 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 active:scale-95'
            }`}
          >
            <span className="text-sm sm:text-base">Send</span>
            <svg className="w-4 h-4 sm:w-5 sm:h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
            </svg>
          </button>
        </div>
        
        {/* Quick Suggestions */}
        <div className="mt-3 sm:mt-4">
          <p className={`text-xs sm:text-sm mb-2 ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
            Try asking about:
          </p>
          <div className="flex flex-wrap gap-1.5 sm:gap-2 overflow-x-auto pb-2 custom-scrollbar">
            {suggestions.map((suggestion, index) => (
              <button
                key={index}
                onClick={() => setInput(suggestion)}
                disabled={loading}
                className={`px-2.5 sm:px-3 py-1.5 sm:py-2 rounded-full text-xs sm:text-sm transition-all duration-200 whitespace-nowrap hover:scale-105 ${
                  loading
                    ? isDarkMode
                      ? 'bg-gray-800 text-gray-600 cursor-not-allowed'
                      : 'bg-gray-100 text-gray-400 cursor-not-allowed'
                    : isDarkMode
                      ? 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                      : 'bg-white text-gray-600 hover:bg-gray-100 border border-gray-200'
                }`}
              >
                {suggestion}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
