import { useState, useEffect, useRef } from 'react';
import Chat from "./Chat";
import './App.css';

export default function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [sessions, setSessions] = useState([]);
  const [currentSession, setCurrentSession] = useState("default");
  const [isLoading, setIsLoading] = useState(true);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const sidebarRef = useRef(null);

  // Close sidebar when clicking outside on mobile
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (window.innerWidth < 1024 && 
          sidebarRef.current && 
          !sidebarRef.current.contains(event.target) &&
          !event.target.closest('.sidebar-toggle')) {
        setIsSidebarOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Load sessions on initial render
  useEffect(() => {
    const loadSessions = async () => {
      try {
        const response = await fetch('http://localhost:8000/sessions');
        if (response.ok) {
          const data = await response.json();
          setSessions(data.sessions || []);
        }
      } catch (error) {
        console.error('Failed to load sessions:', error);
      } finally {
        setIsLoading(false);
      }
    };
    
    loadSessions();
  }, []);

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const createNewSession = async () => {
    try {
      const response = await fetch('http://localhost:8000/sessions/new', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      if (response.ok) {
        const data = await response.json();
        const newSession = data.session_id;
        setCurrentSession(newSession);
        // Reload sessions
        const sessionsResponse = await fetch('http://localhost:8000/sessions');
        if (sessionsResponse.ok) {
          const sessionsData = await sessionsResponse.json();
          setSessions(sessionsData.sessions || []);
        }
        // Close sidebar on mobile after creating new chat
        if (window.innerWidth < 1024) {
          setIsSidebarOpen(false);
        }
      }
    } catch (error) {
      console.error('Failed to create session:', error);
      const newSession = `chat_${Date.now()}`;
      setCurrentSession(newSession);
      setSessions(prev => [
        {
          session_id: newSession,
          title: "New Chat",
          message_count: 0,
          last_activity: new Date().toISOString()
        },
        ...prev
      ]);
    }
  };

  const switchSession = (sessionId) => {
    setCurrentSession(sessionId);
    // Close sidebar on mobile after switching
    if (window.innerWidth < 1024) {
      setIsSidebarOpen(false);
    }
  };

  const deleteSession = async (sessionId, e) => {
    if (e) e.stopPropagation();
    
    if (sessionId === "default") {
      try {
        await fetch(`http://localhost:8000/sessions/${sessionId}/clear`, {
          method: 'POST',
        });
        setCurrentSession("default");
      } catch (error) {
        console.error('Failed to clear default session:', error);
      }
      return;
    }
    
    try {
      await fetch(`http://localhost:8000/sessions/${sessionId}`, {
        method: 'DELETE',
      });
      
      setSessions(prev => prev.filter(s => s.session_id !== sessionId));
      
      if (currentSession === sessionId) {
        setCurrentSession("default");
      }
    } catch (error) {
      console.error('Failed to delete session:', error);
      setSessions(prev => prev.filter(s => s.session_id !== sessionId));
      if (currentSession === sessionId) {
        setCurrentSession("default");
      }
    }
  };

  const formatTime = (timestamp) => {
    if (!timestamp) return 'Just now';
    
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);
    
    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    if (diffDays < 7) return `${diffDays}d ago`;
    return date.toLocaleDateString();
  };

  const formatSessionTitle = (session) => {
    if (!session) return "New Chat";
    if (session.session_id === "default") return "Main Chat";
    return session.title || "New Chat";
  };

  const getCurrentSessionTitle = () => {
    if (currentSession === "default") return "Main Chat";
    
    const current = sessions.find(s => s.session_id === currentSession);
    return current?.title || "New Chat";
  };

  // Get unread message count (simulated)
  const getUnreadCount = (sessionId) => {
    // This would normally come from backend
    return 0;
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${isDarkMode ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900' : 'bg-gradient-to-br from-green-50 via-blue-50 to-purple-50'} islamic-pattern`}>
      {/* Floating Action Button for Mobile */}
      <button
        onClick={toggleSidebar}
        className={`lg:hidden floating-action-btn w-14 h-14 rounded-full flex items-center justify-center z-50 ${
          isDarkMode 
            ? 'bg-gradient-to-r from-green-600 to-emerald-700 text-white' 
            : 'bg-gradient-to-r from-green-500 to-emerald-600 text-white'
        }`}
        aria-label="Toggle sidebar"
      >
        {isSidebarOpen ? (
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        ) : (
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        )}
      </button>

      <div className="container mx-auto px-3 sm:px-4 py-6 sm:py-8">
        <div className="max-w-7xl mx-auto flex flex-col lg:flex-row gap-6 lg:gap-8">
          {/* Sidebar for Chat Sessions */}
          <div 
            ref={sidebarRef}
            className={`lg:w-1/4 rounded-2xl shadow-2xl transition-all duration-300 transform ${
              isDarkMode ? 'bg-gray-800/90 backdrop-blur-sm border border-gray-700' : 'bg-white/90 backdrop-blur-sm border border-gray-200'
            } ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'} 
            fixed lg:relative inset-y-0 left-0 z-40 w-4/5 lg:w-1/4 h-full lg:h-auto overflow-hidden lg:overflow-visible`}
          >
            <div className="p-4 sm:p-6 border-b border-gray-200 dark:border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 sm:w-12 sm:h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center shadow-lg">
                    <span className="text-white text-lg sm:text-xl font-bold">ðŸ•Œ</span>
                  </div>
                  <div>
                    <h2 className={`text-lg sm:text-xl font-bold ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>
                      Chats
                    </h2>
                    <p className={`text-xs sm:text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                      {sessions.length + 1} conversation{(sessions.length + 1) !== 1 ? 's' : ''}
                    </p>
                  </div>
                </div>
                <button
                  onClick={createNewSession}
                  className="px-3 sm:px-4 py-2 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-lg hover:from-green-600 hover:to-emerald-700 transition-all duration-200 text-sm font-medium shadow-lg hover:shadow-xl"
                >
                  <span className="hidden sm:inline">+ New Chat</span>
                  <span className="sm:hidden">+</span>
                </button>
              </div>
            </div>
            
            <div className="p-3 sm:p-4 max-h-[calc(100vh-180px)] lg:max-h-[60vh] overflow-y-auto custom-scrollbar">
              {/* Default Chat */}
              <div
                onClick={() => switchSession("default")}
                className={`p-4 rounded-xl cursor-pointer transition-all duration-200 mb-3 chat-bubble ${
                  currentSession === "default"
                    ? isDarkMode
                      ? 'bg-gradient-to-r from-gray-700 to-gray-800 border-l-4 border-green-500'
                      : 'bg-gradient-to-r from-green-50 to-emerald-50 border-l-4 border-green-500'
                    : isDarkMode
                      ? 'hover:bg-gray-700/70'
                      : 'hover:bg-gray-50/70'
                }`}
              >
                <div className="flex items-start justify-between">
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center">
                      <div className={`w-2 h-2 rounded-full mr-2 ${isDarkMode ? 'bg-green-400' : 'bg-green-500'}`}></div>
                      <h3 className={`font-medium truncate ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>
                        Main Chat
                      </h3>
                    </div>
                    <p className={`text-xs mt-1 ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      Default session â€¢ Always available
                    </p>
                  </div>
                  {getUnreadCount("default") > 0 && (
                    <span className={`ml-2 px-2 py-1 rounded-full text-xs font-medium ${
                      isDarkMode ? 'bg-green-900 text-green-200' : 'bg-green-100 text-green-800'
                    }`}>
                      {getUnreadCount("default")}
                    </span>
                  )}
                </div>
              </div>
              
              {/* Other Chat Sessions */}
              {isLoading ? (
                <div className="space-y-3">
                  {[1, 2, 3].map(i => (
                    <div key={i} className="p-4 rounded-xl mb-3 shimmer" style={{ height: '80px' }}></div>
                  ))}
                </div>
              ) : sessions.length === 0 ? (
                <div className={`text-center py-8 ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                  <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-gradient-to-r from-green-100 to-emerald-100 dark:from-green-900/30 dark:to-emerald-900/30 flex items-center justify-center">
                    <svg className="w-8 h-8 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z" />
                    </svg>
                  </div>
                  <p className="text-sm font-medium mb-1">No chats yet</p>
                  <p className="text-xs">Create your first chat to get started</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {sessions.map((session) => (
                    <div
                      key={session.session_id}
                      onClick={() => switchSession(session.session_id)}
                      className={`p-4 rounded-xl cursor-pointer transition-all duration-200 chat-bubble ${
                        currentSession === session.session_id
                          ? isDarkMode
                            ? 'bg-gradient-to-r from-gray-700 to-gray-800 border-l-4 border-green-500'
                            : 'bg-gradient-to-r from-green-50 to-emerald-50 border-l-4 border-green-500'
                          : isDarkMode
                            ? 'hover:bg-gray-700/70'
                            : 'hover:bg-gray-50/70'
                      }`}
                    >
                      <div className="flex items-start justify-between">
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center">
                            <div className={`w-2 h-2 rounded-full mr-2 ${
                              session.message_count > 0 
                                ? isDarkMode ? 'bg-green-400' : 'bg-green-500'
                                : isDarkMode ? 'bg-gray-500' : 'bg-gray-400'
                            }`}></div>
                            <h3 className={`font-medium truncate ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>
                              {formatSessionTitle(session)}
                            </h3>
                          </div>
                          <div className="flex items-center mt-1 space-x-3">
                            <span className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                              {session.message_count || 0} message{(session.message_count || 0) !== 1 ? 's' : ''}
                            </span>
                            <span className={`text-xs px-2 py-0.5 rounded-full ${
                              isDarkMode ? 'bg-gray-700 text-gray-300' : 'bg-gray-100 text-gray-600'
                            }`}>
                              {formatTime(session.last_activity)}
                            </span>
                          </div>
                        </div>
                        <button
                          onClick={(e) => deleteSession(session.session_id, e)}
                          className={`ml-2 p-1.5 rounded-full hover:bg-opacity-20 transition-colors ${
                            isDarkMode
                              ? 'hover:bg-red-500 text-gray-400 hover:text-red-400'
                              : 'hover:bg-red-100 text-gray-500 hover:text-red-600'
                          }`}
                          title="Delete chat"
                        >
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                          </svg>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Overlay for mobile sidebar */}
          {isSidebarOpen && (
            <div 
              className="fixed inset-0 bg-black/50 z-30 lg:hidden"
              onClick={() => setIsSidebarOpen(false)}
            ></div>
          )}

          {/* Main Chat Area */}
          <div className="lg:w-3/4 flex-1">
            {/* Header */}
            <div className="text-center mb-6 sm:mb-8">
              <div className="flex justify-between items-center mb-4 sm:mb-6">
                <button
                  onClick={toggleSidebar}
                  className="lg:hidden sidebar-toggle w-10 h-10 rounded-full flex items-center justify-center transition-all duration-300"
                  aria-label="Toggle sidebar"
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                  </svg>
                </button>
                <div className="flex-1 flex items-center justify-center space-x-2 sm:space-x-3">
                  <div className="w-10 h-10 sm:w-12 sm:h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center shadow-lg">
                    <span className="text-white text-lg sm:text-xl font-bold">ðŸ•Œ</span>
                  </div>
                  <div>
                    <h1 className="text-2xl sm:text-3xl font-bold gradient-text">
                      Islamic AI Assistant
                    </h1>
                    <p className="text-xs sm:text-sm text-green-600 dark:text-green-400 font-medium mt-1">
                      Authentic Islamic Knowledge
                    </p>
                  </div>
                </div>
                <button
                  onClick={toggleTheme}
                  className={`w-10 h-10 rounded-full flex items-center justify-center transition-all duration-300 ${
                    isDarkMode 
                      ? 'bg-gray-700 text-yellow-300 hover:bg-gray-600 shadow-lg' 
                      : 'bg-white text-gray-600 hover:bg-gray-100 shadow-lg'
                  }`}
                  aria-label="Toggle theme"
                >
                  {isDarkMode ? 'â˜€ï¸' : 'ðŸŒ™'}
                </button>
              </div>
              <p className={`text-sm sm:text-base px-4 ${isDarkMode ? 'text-gray-300' : 'text-gray-600'} max-w-2xl mx-auto`}>
                Ask questions about Islam based on authentic sources from Quran, Hadith, and classical scholarship
              </p>
              {currentSession && (
                <div className={`mt-3 px-4 ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                  <div className="inline-flex items-center space-x-2 bg-gradient-to-r from-green-50 to-emerald-50 dark:from-gray-800 dark:to-gray-700 px-3 py-1.5 rounded-full">
                    <span className="text-xs sm:text-sm">
                      Active: <span className="font-semibold text-green-600 dark:text-green-400">
                        {getCurrentSessionTitle()}
                      </span>
                    </span>
                    {currentSession !== "default" && (
                      <button
                        onClick={(e) => deleteSession(currentSession, e)}
                        className="text-xs text-red-500 hover:text-red-600 bg-red-50 dark:bg-red-900/30 px-2 py-0.5 rounded"
                      >
                        Delete
                      </button>
                    )}
                  </div>
                </div>
              )}
            </div>

            {/* Main Chat Container */}
            <div className={`rounded-2xl shadow-2xl overflow-hidden transition-all duration-300 h-[60vh] sm:h-[65vh] lg:h-[70vh] ${
              isDarkMode 
