import { useState, useEffect } from 'react';
import Chat from "./Chat";
import './App.css';

export default function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [sessions, setSessions] = useState([]);
  const [currentSession, setCurrentSession] = useState("default");
  const [isLoading, setIsLoading] = useState(true);

  // Load sessions on initial render
  useEffect(() => {
    const loadSessions = async () => {
      try {
        const response = await fetch('http://localhost:8000/sessions');
        if (response.ok) {
          const data = await response.json();
          setSessions(data.sessions || []);
        }
      } catch (error) {
        console.error('Failed to load sessions:', error);
      } finally {
        setIsLoading(false);
      }
    };
    
    loadSessions();
  }, []);

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const createNewSession = async () => {
    try {
      const response = await fetch('http://localhost:8000/sessions/new', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      if (response.ok) {
        const data = await response.json();
        const newSession = data.session_id;
        setCurrentSession(newSession);
        // Add placeholder session to list
        setSessions(prev => [
          {
            session_id: newSession,
            title: "New Chat",
            message_count: 0,
            last_activity: new Date().toISOString()
          },
          ...prev
        ]);
      }
    } catch (error) {
      console.error('Failed to create session:', error);
      // Fallback: create session locally
      const newSession = `chat_${Date.now()}`;
      setCurrentSession(newSession);
      setSessions(prev => [
        {
          session_id: newSession,
          title: "New Chat",
          message_count: 0,
          last_activity: new Date().toISOString()
        },
        ...prev
      ]);
    }
  };

  const switchSession = (sessionId) => {
    setCurrentSession(sessionId);
  };

  const deleteSession = async (sessionId, e) => {
    if (e) e.stopPropagation();
    
    if (sessionId === "default") {
      // Clear default session instead of deleting
      try {
        await fetch(`http://localhost:8000/sessions/${sessionId}/clear`, {
          method: 'POST',
        });
        setCurrentSession("default");
      } catch (error) {
        console.error('Failed to clear default session:', error);
      }
      return;
    }
    
    try {
      await fetch(`http://localhost:8000/sessions/${sessionId}`, {
        method: 'DELETE',
      });
      
      setSessions(prev => prev.filter(s => s.session_id !== sessionId));
      
      if (currentSession === sessionId) {
        setCurrentSession("default");
      }
    } catch (error) {
      console.error('Failed to delete session:', error);
      // Local fallback
      setSessions(prev => prev.filter(s => s.session_id !== sessionId));
      if (currentSession === sessionId) {
        setCurrentSession("default");
      }
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);
    
    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    if (diffDays < 7) return `${diffDays}d ago`;
    return date.toLocaleDateString();
  };

  const formatSessionTitle = (session) => {
    if (session.session_id === "default") return "Main Chat";
    return session.title || "New Chat";
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${isDarkMode ? 'bg-gradient-to-br from-gray-900 to-gray-800' : 'bg-gradient-to-br from-green-50 via-blue-50 to-purple-50'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-6xl mx-auto flex flex-col lg:flex-row gap-8">
          {/* Sidebar for Chat Sessions */}
          <div className={`lg:w-1/4 rounded-2xl shadow-xl transition-all duration-300 ${isDarkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-200'}`}>
            <div className="p-6 border-b border-gray-200 dark:border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <h2 className={`text-xl font-bold ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>
                  Chats
                </h2>
                <button
                  onClick={createNewSession}
                  className="px-4 py-2 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-lg hover:from-green-600 hover:to-emerald-700 transition-all duration-200 text-sm font-medium"
                >
                  + New Chat
                </button>
              </div>
              <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                {sessions.length + 1} chat{(sessions.length + 1) !== 1 ? 's' : ''}
              </p>
            </div>
            
            <div className="p-4 max-h-[60vh] overflow-y-auto">
              {/* Default Chat */}
              <div
                onClick={() => switchSession("default")}
                className={`p-4 rounded-xl cursor-pointer transition-all duration-200 mb-3 ${
                  currentSession === "default"
                    ? isDarkMode
                      ? 'bg-gray-700 border-l-4 border-green-500'
                      : 'bg-green-50 border-l-4 border-green-500'
                    : isDarkMode
                      ? 'hover:bg-gray-700'
                      : 'hover:bg-gray-50'
                }`}
              >
                <div className="flex items-start justify-between">
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center">
                      <div className={`w-2 h-2 rounded-full mr-2 ${isDarkMode ? 'bg-green-400' : 'bg-green-500'}`}></div>
                      <h3 className={`font-medium truncate ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>
                        Main Chat
                      </h3>
                    </div>
                    <p className={`text-xs mt-1 ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      Always available ‚Ä¢ Context memory active
                    </p>
                  </div>
                  <button
                    onClick={(e) => deleteSession("default", e)}
                    className={`ml-2 p-1 rounded-full hover:bg-opacity-20 ${
                      isDarkMode
                        ? 'hover:bg-gray-600 text-gray-400 hover:text-gray-300'
                        : 'hover:bg-gray-200 text-gray-500 hover:text-gray-700'
                    }`}
                    title="Clear chat"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                    </svg>
                  </button>
                </div>
              </div>
              
              {/* Other Chat Sessions */}
              {isLoading ? (
                <div className="flex justify-center py-8">
                  <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-green-500"></div>
                </div>
              ) : (
                sessions.map((session) => (
                  <div
                    key={session.session_id}
                    onClick={() => switchSession(session.session_id)}
                    className={`p-4 rounded-xl cursor-pointer transition-all duration-200 mb-3 ${
                      currentSession === session.session_id
                        ? isDarkMode
                          ? 'bg-gray-700 border-l-4 border-green-500'
                          : 'bg-green-50 border-l-4 border-green-500'
                        : isDarkMode
                          ? 'hover:bg-gray-700'
                          : 'hover:bg-gray-50'
                    }`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1 min-w-0">
                        <h3 className={`font-medium truncate ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>
                          {formatSessionTitle(session)}
                        </h3>
                        <div className="flex items-center mt-1 space-x-2">
                          <span className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                            {session.message_count || 0} message{(session.message_count || 0) !== 1 ? 's' : ''}
                          </span>
                          <span className="text-gray-400">‚Ä¢</span>
                          <span className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                            {formatTime(session.last_activity)}
                          </span>
                        </div>
                      </div>
                      <button
                        onClick={(e) => deleteSession(session.session_id, e)}
                        className={`ml-2 p-1 rounded-full hover:bg-opacity-20 ${
                          isDarkMode
                            ? 'hover:bg-red-500 text-gray-400 hover:text-red-400'
                            : 'hover:bg-red-100 text-gray-500 hover:text-red-600'
                        }`}
                        title="Delete chat"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Main Chat Area */}
          <div className="lg:w-3/4">
            {/* Header */}
            <div className="text-center mb-8">
              <div className="flex justify-between items-center mb-6">
                <div className="w-10"></div> {/* Spacer */}
                <div className="flex items-center justify-center space-x-3">
                  <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full flex items-center justify-center shadow-lg">
                    <span className="text-white text-xl font-bold">üïå</span>
                  </div>
                  <h1 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
                    Islamic AI Assistant
                  </h1>
                </div>
                <button
                  onClick={toggleTheme}
                  className={`w-10 h-10 rounded-full flex items-center justify-center transition-all duration-300 ${
                    isDarkMode 
                      ? 'bg-gray-700 text-yellow-300 hover:bg-gray-600' 
                      : 'bg-white text-gray-600 hover:bg-gray-100 shadow-md'
                  }`}
                >
                  {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
                </button>
              </div>
              <p className={`text-lg ${isDarkMode ? 'text-gray-300' : 'text-gray-600'} max-w-2xl mx-auto`}>
                Ask questions about Islam based on authentic sources from Quran, Hadith, and classical scholarship
              </p>
              {currentSession && (
                <p className={`text-sm mt-2 ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                  Active chat: <span className="font-medium">
                    {currentSession === "default" 
                      ? "Main Chat" 
                      : sessions.find(s => s.session_id === currentSession)?.title || "New Chat"}
                  </span>
                  {currentSession !== "default" && (
                    <span className="ml-4">
                      <button
                        onClick={(e) => deleteSession(currentSession, e)}
                        className="text-red-500 hover:text-red-600 text-xs"
                      >
                        Delete this chat
                      </button>
                    </span>
                  )}
                </p>
              )}
            </div>

            {/* Main Chat Container */}
            <div className={`rounded-2xl shadow-2xl overflow-hidden transition-all duration-300 ${
              isDarkMode 
                ? 'bg-gray-800 border border-gray-700' 
                : 'bg-white border border-gray-200'
            }`}>
              <Chat isDarkMode={isDarkMode} sessionId={currentSession} />
            </div>

            {/* Footer */}
            <div className="text-center mt-6">
              <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                Answers based on authentic Islamic sources ‚Ä¢ May Allah guide us to beneficial knowledge
              </p>
              <div className="flex justify-center items-center space-x-4 mt-2">
                <span className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                  ‚Ä¢ Session memory active
                </span>
                <span className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                  ‚Ä¢ Context-aware responses
                </span>
                <span className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                  ‚Ä¢ Chat history preserved
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
