import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

// Add token to all requests
axios.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Handle token expiration
axios.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const askQuestion = async (question, sessionId = null) => {
  try {
    const response = await axios.post(`${BACKEND_URL}/ask`, { 
      question, 
      session_id: sessionId 
    });
    return response.data;
  } catch (error) {
    console.error("Backend error:", error);
    return {
      answer: "As-salamu alaykum. I apologize, but I'm having trouble connecting to the Islamic knowledge base. Please try again later.",
      session_id: null,
      session_name: "Error"
    };
  }
};

export const getSessions = async () => {
  try {
    const response = await axios.get(`${BACKEND_URL}/sessions`);
    return response.data.sessions || [];
  } catch (error) {
    console.error("Error fetching sessions:", error);
    return [];
  }
};

export const createNewSession = async () => {
  try {
    const response = await axios.post(`${BACKEND_URL}/sessions/new`);
    return response.data.session_id;
  } catch (error) {
    console.error("Error creating session:", error);
    return null;
  }
};

export const deleteSession = async (sessionId) => {
  try {
    await axios.delete(`${BACKEND_URL}/sessions/${sessionId}`);
    return true;
  } catch (error) {
    console.error("Error deleting session:", error);
    return false;
  }
};

export const getSessionMessages = async (sessionId) => {
  try {
    const response = await axios.get(`${BACKEND_URL}/sessions/${sessionId}/messages`);
    return response.data.messages || [];
  } catch (error) {
    console.error("Error fetching messages:", error);
    return [];
  }
};

// Profile API calls
export const getCurrentUser = async () => {
  try {
    const response = await axios.get(`${BACKEND_URL}/auth/me`);
    return response.data;
  } catch (error) {
    console.error("Error getting current user:", error);
    throw error;
  }
};

export const getUserProfile = async () => {
  try {
    const response = await axios.get(`${BACKEND_URL}/profile/me`);
    return response.data;
  } catch (error) {
    console.error("Error getting profile:", error);
    throw error;
  }
};

export const updateProfile = async (profileData) => {
  try {
    const response = await axios.put(`${BACKEND_URL}/profile/me`, profileData);
    return response.data;
  } catch (error) {
    console.error("Error updating profile:", error);
    throw error;
  }
};

export const uploadProfilePicture = async (file) => {
  const formData = new FormData();
  formData.append('file', file);
  
  try {
    const response = await axios.post(`${BACKEND_URL}/profile/picture`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' }
    });
    return response.data.picture_url;
  } catch (error) {
    console.error("Error uploading picture:", error);
    throw error;
  }
};

export const getUserStats = async () => {
  try {
    const response = await axios.get(`${BACKEND_URL}/profile/stats`);
    return response.data;
  } catch (error) {
    console.error("Error getting stats:", error);
    throw error;
  }
};
