import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

export const askQuestion = async (question, sessionId = null) => {
  try {
    const response = await axios.post(`${BACKEND_URL}/ask`, { 
      question, 
      session_id: sessionId 
    });
    return response.data;
  } catch (error) {
    console.error("Backend error:", error);
    return {
      answer: "As-salamu alaykum. I apologize, but I'm having trouble connecting to the Islamic knowledge base. Please try again later.",
      session_id: null
    };
  }
};

export const getSessions = async () => {
  try {
    const response = await axios.get(`${BACKEND_URL}/sessions`);
    return response.data.sessions || [];
  } catch (error) {
    console.error("Error fetching sessions:", error);
    return [];
  }
};

export const getSession = async (sessionId) => {
  try {
    const response = await axios.get(`${BACKEND_URL}/sessions/${sessionId}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching session:", error);
    return null;
  }
};

export const createNewSession = async () => {
  try {
    const response = await axios.post(`${BACKEND_URL}/sessions/new`);
    return response.data.session_id;
  } catch (error) {
    console.error("Error creating session:", error);
    return null;
  }
};

export const deleteSession = async (sessionId) => {
  try {
    await axios.delete(`${BACKEND_URL}/sessions/${sessionId}`);
    return true;
  } catch (error) {
    console.error("Error deleting session:", error);
    return false;
  }
};

// Profile API calls (mock implementations for now)
export const getCurrentUser = async () => {
  // Return mock user data
  return {
    name: "User",
    email: "user@example.com",
    picture: "https://ui-avatars.com/api/?name=User&background=10b981&color=fff&size=150",
    created_at: new Date().toISOString(),
    last_login: new Date().toISOString(),
    preferences: {},
    settings: {
      theme: "light",
      language: "en",
      notifications: true,
      font_size: "medium"
    }
  };
};

export const getUserProfile = async () => {
  return await getCurrentUser();
};

export const getUserStats = async () => {
  return {
    total_chats: 0,
    total_messages: 0,
    favorite_topics: ["Prayer", "Fasting", "Zakat"],
    joined_date: new Date().toISOString(),
    last_active: new Date().toISOString()
  };
};

export const updateProfile = async (profileData) => {
  console.log("Profile updated:", profileData);
  return profileData;
};

export const uploadProfilePicture = async (file) => {
  console.log("Uploading picture:", file.name);
  return "https://ui-avatars.com/api/?name=User&background=10b981&color=fff&size=150";
};
